package assignment1.loopAssignment;

public class Assginment5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Check number is multiple of 7 then quit 
		for (int i=1;i<=10;i++) {
			if(i%7==0) {
				break;
			}else {
				System.out.println(i);
			}
				
		}
	}

}
