package assignment2;

public class ArrayLoopAssignment1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=0; i<4;i++){
			for (int j=0;j<10;j++) {
				System.out.print(i);
				System.out.print(j);
				System.out.print(" ");
			}
		System.out.println();
		}
	}

}
